import React, { Component } from 'react';

import EspaceRecruteur from './EspaceClient/EspaceRecruteur';
import ProfilRecruteur from '../Employeur/Profil/ProfilRecruteur';
class Employeur extends Component {
  state={
      recruteur: {},
      token: ''
  }

  componentDidMount = () => {
      axios.get(`recruteurs/${sessionStorage.id}`).then(
        resp => {
            console.log(resp.data.recruteur)
          this.setState({
            recruteur: resp.data.recruteur
          })
          this.setState({
              token: resp.data.token
          })
        }
      )
    }
    render() {
      let recruteur;

      if(sessionStorage.id){
        recruteur = (
          <>
            <ProfilRecruteur/>
          </>
        )
      }
        return (

          <>
			      <EspaceRecruteur />
          </>  
        );
    }
}

export default Employeur;
