import React, { Component } from 'react'
import {Formik, Form, Field} from 'formik';
import axios from 'axios';

export class Forgot extends Component {
    state={
        errors: ''
    }
    render() {
        return (
            <div>
                <a href="#" data-toggle="modal" data-target="#forgotpassword">Mot de passe oubli√© !</a>

                <div class="modal fade" id="forgotpassword" tabindex="-1" aria-labelledby="exampleModalLabel" aria-hidden="true">
                    <div class="modal-dialog">
                        <div class="modal-content">
                        <div class="modal-header">
                            <h5 class="modal-title" id="exampleModalLabel">Veuiller saisir votre addresse Email ci dessous</h5>
                            <button type="button" class="close" data-dismiss="modal" aria-label="Close">
                            <span aria-hidden="true">&times;</span>
                            </button>
                        </div>
                        <div class="modal-body">
                            <Formik
                            initialValues={{
                                email: ''
                            }}

                            onSubmit={(values, e ) => {
                                e.preventDefault();
                                axios.post('/forgot', values).then(response => {
                                    console.log(response)
                                    if(response.status == 202){
                                        this.setState({
                                            errors: response.data.errors
                                        })
                                    }
                                })
                            }}
                            >				

                                <Form className="d-flex bd-highlight" onSubmit={this.onSubmit} >
                                    <Field type="email" className="form-control" name="email" placeholder="Votre addresse email" required/>
                                    <Field type="submit" value="Valider" className="btn btn-primary" />
                                </Form>

                            </Formik>
                        </div>
                        {this.state.errors}
                        <div class="modal-footer">
                            <span>Apr√©s l'action Valider vous allez recevoire votre email dans votre boite E-mail</span>	
                        </div>
                        </div>
                    </div>
                </div>

            </div>
        )
    }
}

export default Forgot
