import React, { Component } from 'react';
import { Link } from 'react-router-dom';

class Navbar extends Component {
    handleClick = () => {
        sessionStorage.clear()
        localStorage.clear(window.location.reload())
    }

    handleCheck = () => {
        alert('veuiller vous deconnecter ;-)');
    }
    render() {
        let button;
        let recruteur;
        let candidat;

        if(sessionStorage.id){

            button = (
                <>
                    <li className="nav-item">
                        <Link className="nav-link" onClick={this.handleClick}>Deconnexion</Link>
                    </li>
                </>
            )
        }

        if(sessionStorage.id){

            recruteur = (
                <>
                    <li className="nav-item">
                        <Link className="nav-link" onClick={this.handleCheck}>recruteur</Link>
                    </li>
                </>
            )
        }else{
            recruteur = (
                <>
                    <li className="nav-item">
                        <Link to="/recruteur" className="nav-link">recruteur</Link>
                    </li>
                </>
            )
        }

        if(localStorage.id){
            button = (
                <>
                    <li className="nav-item">
                        <Link className="nav-link" onClick={this.handleClick}>Deconnexion</Link>
                    </li>
                </>
            )
        }

        if(localStorage.id){
            candidat = (
                <>
                    <li className="nav-item">
                        <Link className="nav-link" onClick={this.handleCheck}>Candidat</Link>
                    </li>
                </>
            )
        }else(
            candidat = (
                <>
                    <li className="nav-item">
                        <Link to="/candidat" className="nav-link">Candidat</Link>
                    </li>
                </>
            )
        )
        return (
            <div>
                <nav className="navbar navbar-expand-lg navbar-dark ftco_navbar bg-dark ftco-navbar-light" id="ftco-navbar">
                    <div className="container-fluid">

                    <button className="navbar-toggler" type="button" data-toggle="collapse" data-target="#ftco-nav" aria-controls="ftco-nav" aria-expanded="false" aria-label="Toggle navigation">
                        <span className="fa fa-bars"></span> Menu
                    </button>
                    <div className="collapse navbar-collapse" id="ftco-nav">
                        <ul className="navbar-nav m-auto">
                            <li className="nav-item active">
                                <Link to="/" className="nav-link">Accueil</Link>	
                            </li>
                            {candidat}
                            {recruteur}
                            <li className="nav-item">
                                <Link to="/contact" className="nav-link">Contact</Link>
                            </li>
                            <li className="nav-item">
                                <Link to="/condition_general" className="nav-link">Condition general</Link>
                            </li>
                            {button}
                        </ul>
                    </div>
                    </div>
                </nav>
                <div className="col-sm-4 col-md">
                            <div className="ftco-footer-widget mb-8">
                            <h2 className="ftco-heading-2">SLM-I 
                            </h2>
                            <div className="map-responsive">
                            <address className="b-info__map"><iframe src="https://www.google.com/maps/embed?pb=!1m14!1m12!1m3!1d5335.58373869679!2d47.53726100500432!3d-18.98322666771267!2m3!1f0!2f0!3f0!3m2!1i1024!2i768!4f13.1!5e0!3m2!1sfr!2smg!4v1608356920542!5m2!1sfr!2smg" width="500" height="200" frameborder="0" style={{border:'0'}} allowfullscreen="" aria-hidden="false" tabindex="0"></iframe>
                            </address>
                        </div>


                            <div className="block-23 mb-3">
                                <ul>
                                <li><span className="icon fa fa-map marker"></span><span className="text">Andoaranofotsy EF 2A Tanànarive Antsimodrano</span></li>
                                <li><a href="#"><span className="icon fa fa-phone"></span><span className="text">+261 32 00 120 21</span></a></li>
                                <li><a href="#"><span className="icon fa fa-paper-plane pr-4"></span><span className="text"> slm-i@gmail.com</span></a></li>
                                </ul>
                            </div>
                        </div>
                        </div>
            </div>
        );
    }
}

export default Navbar;
