import React, { Component } from 'react'
import {Formik, Form, Field} from 'formik'
import axios from 'axios'

export class MoreProfile extends Component {
    state={
        experience: null
    }

    handleChange = (e) => {
        this.setState({
            experience: e.target.value
        })
    }
    render() {
        return (
            <div>
                <h4 className="marge">Ajouter plus d'information <a href="#"data-toggle="modal" data-target="#exampleModal" data-whatever="@mdo">
                    <i className="fa fa-plus" aria-hidden="true"></i></a></h4>
                    <div class="modal fade" id="exampleModal" tabindex="-1" aria-labelledby="exampleModalLabel" aria-hidden="true">
                <div class="modal-dialog">
                    <div class="modal-content">
                    <div class="modal-header">
                        <h5 class="modal-title" id="exampleModalLabel">Ajouter d'autre profile</h5>
                        <button type="button" class="close" data-dismiss="modal" aria-label="Close">
                        <span aria-hidden="true">&times;</span>
                        </button>
                    </div>
                    <div class="modal-body">
                        <Formik
                            initialValues={{
                                etude: '',
                                formation: '',
                                experience: null
                            }}

                            onSubmit={(values, {resetForm}) => {
                                const formData = new FormData();
                                formData.append('etude', values.etude)
                                formData.append('formation', values.formation)
                                formData.append('experience', this.state.experience)

                                axios.post('plusinfos', formData).then(resp => {
                                    if(resp.status == 200){
                                        resetForm();
                                    }
                                })
                            }}
                        >
                            <Form>
                                <div class="mb-3">
                                    <label for="recipient-name" class="col-form-label">Etudes</label>
                                    <Field type="text" class="form-control" id="recipient-name"/>
                                </div>
                                <div class="mb-3">
                                    <label for="recipient-name" class="col-form-label">Formation</label>
                                    <Field type="text" class="form-control" id="recipient-name"/>
                                </div>
                                <div class="mb-3">
                                    <label for="message-text" class="col-form-label">Experience proffessionnel</label>
                                    <textarea class="form-control" id="message-text" onChange={this.handleChange}></textarea>
                                </div>
                            </Form>
                        </Formik>
                    </div>
                    <div class="modal-footer">
                        <button type="button" class="btn btn-primary">Valider</button>
                    </div>
                    </div>
                </div>
            </div>
            </div>
        )
    }
}

export default MoreProfile
