import React, { Component } from 'react'
import {Formik, Form, Field} from 'formik';
import axios from 'axios';

export class Forgot extends Component {
    state={
        errors: '',
        email: ''
    }

    handleChange = e => {

        this.setState({
            email: e.target.value
        })
    }

    handleSubmit = e => {
        e.preventDefault()
        axios.post('forgot', this.state.email).then(response => {
            if(response.status == 202){
                console.log(response)
                this.setState({
                    errors: response.data.errors
                })
            }
        })
    }
    render() {
        return (
            <div>
                <a href="#" data-toggle="modal" data-target="#forgotpassword">Mot de passe oubliÃ© !</a>

                <div class="modal fade" id="forgotpassword" tabindex="-1" aria-labelledby="exampleModalLabel" aria-hidden="true">
                    <div class="modal-dialog">
                        <div class="modal-content">
                        <div class="modal-header">
                            <h5 class="modal-title" id="exampleModalLabel">Veuiller saisir votre addresse Email ci dessous</h5>
                            <button type="button" class="close" data-dismiss="modal" aria-label="Close">
                            <span aria-hidden="true">&times;</span>
                            </button>
                        </div>
                        <div class="modal-body">


                            <form className="d-flex bd-highlight" onSubmit={this.handleSubmit} >
                                <input type="email" className="form-control" onChange={this.handleChange} placeholder="Votre addresse email" required/>
                                <input type="submit" value="Valider" className="btn btn-primary" />
                            </form>

                        </div>
                        <div className="message">{this.state.errors}</div>
                        <div class="modal-footer">
                            <span>AprÃ©s l'action Valider vous allez recevoire votre mot de passe dans votre boite E-mail.</span>	
                        </div>
                        </div>
                    </div>
                </div>

            </div>
        )
    }
}

export default Forgot
